(function($){wp.customize.bind("ready",function(){wp.customize("blogname",function(value){value.bind(function(newval){$("#site-title a").html(newval)})});wp.customize("blogdescription",function(value){value.bind(function(newval){$(".site-description").html(newval)})});wp.customize("header_textcolor",function(value){value.bind(function(newval){$("#site-title a").css("color",newval)})});wp.customize("background_color",function(value){value.bind(function(newval){$("body").css("background-color",newval)})});wp.customize("link_textcolor",function(value){value.bind(function(newval){$("a").css("color",newval)})});for(var i=1;i<3;i++){wp.customize("setting_section_head_"+i,function(value){value.bind(function(newVal){console.log(newVal);$(".text-hero .head-"+i).text(newVal)})})}})})(jQuery);